$(document).ready(function() {
  fetch(
    "https://newsapi.org/v2/top-headlines?country=in&apiKey=301f8a69a50b4f8c8bfbd6a2bfd6687c"
  )
    .then(res => res.json())
    .then(data => {
      let newsData = data.articles;
      let newsItems = newsData.map(item => {
        let timeFromNow = moment(
          item.publishedAt,
          "YYYY-MM-DD HH:mm:ss"
        ).fromNow();
        let updatedContent = item.content
          ? item.content.substring(0, 250) + "..."
          : "";
        return `
        <div
          class="news-item bg-dark flex-column align-items-start mb-4 p-4"
        >
          <div class="row">
            <div class="news-image col-lg-4 col-md-4 col-xs-12 ">
              <img
                class="img-thumbnail"
                src="${
                  item.urlToImage
                    ? item.urlToImage
                    : "data:image/png;base64,"
                }"
              />
            </div>
            <div class="news-content col-lg-8 col-md-8 col-xs-12">
              <div class="d-flex w-100 justify-content-between">
                <h4 class="mt-3 text-left">${item.title}</h4>
              </div>
              <p class="mt-2 text-left ">
                ${updatedContent}
              </p>
              <a class="btn btn-outline-info" href="${item.url}">Read more</a>

              <div class="news-metadata">
              <small class="text-muted">${
                item.author ? item.author : "Anonymous"
              }</small>
              <small class="text-muted">${timeFromNow}</small>
              </div>
            </div>
          </div>
        </div>
        
        `;
      });

      newsItems = newsItems.join("");
      $(".news-list").html(newsItems);
      $("footer p").text("Designed and Maintained by Rahul Mohata");

      $(window).scroll(function() {
        /* Check the location of each desired element */
        $(".news-item").each(function(i) {
          var bottom_of_object = $(this).position().top + $(this).outerHeight();
          var bottom_of_window = $(window).scrollTop() + $(window).height();

          /* If the object is completely visible in the window, fade it it */
          if (bottom_of_window > bottom_of_object - 320) {
            $(this).animate({ opacity: "1" }, 800);
          }
        });
      });
    });
});
